import React, { useState } from "react";
import Position from "../components/position";
import "./442style.css";
import axios from "axios";

function Formation442() {

  const players = [
    "player1",
    "player2",
    "player3",
    "player4",
    "player5",
    "player6",
    "player7",
    "player8",
    "player9",
    "player10",
    "player11"
  ];

  const roles = [
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role"
  ]

  const roleToPositionMapping = {
    [players[0]]: roles[0],
    [players[1]]: roles[1],
    [players[2]]: roles[2],
    [players[3]]: roles[3],
    [players[4]]: roles[4],
    [players[5]]: roles[5],
    [players[6]]: roles[6],
    [players[7]]: roles[7],
    [players[8]]: roles[8],
    [players[9]]: roles[9],
    [players[10]]: roles[10]
  };

  const handleRoleChange = (player, newRole) => {
    // Update the role for the specified player
    roleToPositionMapping[player] = newRole;

    // Log the updated mapping
    console.log("Updated role mapping:", roleToPositionMapping);
  };


  const [bestTeam, setBestTeam] = useState(null);

  const handleGetBestTeam = () => {
    const selectedRoles = roleToPositionMapping;

    axios.post('/get_best_team', { selected_roles: selectedRoles })
      .then(response => {
        setBestTeam(response.data);
      })
      .catch(error => {
        console.error('Error fetching the best team:', error);
      });
  };



  return (
    <div className="formation442">
      <div className="attackLane442">
      <Position playerName={players[0]} positionName="ATTACK" onRoleChange={(role) => handleRoleChange(players[0], role)} />
      <Position playerName={players[1]} positionName="ATTACK" onRoleChange={(role) => handleRoleChange(players[1], role)} />
      </div>
      <div className="midLane442">
        <Position playerName={players[2]} positionName="MID" onRoleChange={(role) => handleRoleChange(players[2], role)}/>
        <Position playerName={players[3]} positionName="MID" onRoleChange={(role) => handleRoleChange(players[3], role)}/>
        <Position playerName={players[4]} positionName="MID" onRoleChange={(role) => handleRoleChange(players[4], role)}/>
        <Position playerName={players[5]} positionName="MID" onRoleChange={(role) => handleRoleChange(players[5], role)} />
      </div>
      <div className="backLane442">
      <Position playerName={players[6]} positionName="LAT" onRoleChange={(role) => handleRoleChange(players[6], role)}/>
        <Position playerName={players[7]} positionName="DEF" onRoleChange={(role) => handleRoleChange(players[7], role)}/>
        <Position playerName={players[8]} positionName="DEF"onRoleChange={(role) => handleRoleChange(players[8], role)}/>
        <Position playerName={players[9]} positionName="LAT"onRoleChange={(role) => handleRoleChange(players[9], role)}/>
      </div>
      <div className="gkLane442">
      <Position playerName={players[10]} positionName="GK"onRoleChange={(role) => handleRoleChange(players[10], role)}/>
      </div>
      <button onClick={handleGetBestTeam}>Get Best Team</button>

      {bestTeam && (
          <div>
            <h2>Best Team</h2>
            <pre>{JSON.stringify(bestTeam, null, 2)}</pre>
          </div>
        )}
    </div>
  );
}

export default Formation442;
