import React from 'react';
import Position from "../components/position";
import "./352style.css";
function Formation352() {

  const players = [
    "player1",
    "player2",
    "player3",
    "player4",
    "player5",
    "player6",
    "player7",
    "player8",
    "player9",
    "player10",
    "player11"
  ];

  const roles = [
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role"
  ]

  const roleToPositionMapping = {
    [players[10]]: [roles[10]],
    [players[9]]: [roles[9]],
    [players[8]]: [roles[8]],
    [players[7]]: [roles[7]],
    [players[6]]: [roles[6]],
    [players[2]]: [roles[2]],
    [players[5]]: [roles[5]],
    [players[4]]: [roles[4]],
    [players[3]]: [roles[3]],
    [players[0]]: [roles[0]],
    [players[1]]: [roles[1]],
  };

  const handleRoleChange = (player, newRole) => {
    // Update the role for the specified player
    roleToPositionMapping[player] = newRole;

    // Log the updated mapping
    console.log("Updated role mapping:", roleToPositionMapping);
  };


  return (
    <div className="formation352">
      <div className="attackLane352">
      <Position playerName={players[0]} positionName="ATTACK" onRoleChange={(role) => handleRoleChange(players[0], role)} />
      <Position playerName={players[1]} positionName="ATTACK" onRoleChange={(role) => handleRoleChange(players[1], role)} />
      </div>
      <div className="midLane352">
        <Position playerName={players[2]} positionName="LAT" onRoleChange={handleRoleChange}  />

        <Position playerName={players[3]} positionName="MID" onRoleChange={handleRoleChange}  />
        <Position playerName={players[4]} positionName="MID" onRoleChange={handleRoleChange}  />
        <Position playerName={players[5]} positionName="MID" onRoleChange={handleRoleChange}  />
        <Position playerName={players[6]} positionName="LAT" onRoleChange={handleRoleChange}  />
      </div>
      <div className="backLane352">
        <Position playerName={players[7]} positionName="DEF" onRoleChange={(role) => handleRoleChange(players[7], role)}/>
        <Position playerName={players[8]} positionName="DEF"onRoleChange={(role) => handleRoleChange(players[8], role)}/>
        <Position playerName={players[9]} positionName="DEF"onRoleChange={(role) => handleRoleChange(players[9], role)}/>
      </div>
      <div className="gkLane352">
      <Position playerName={players[10]} positionName="GK"onRoleChange={(role) => handleRoleChange(players[10], role)}/>      </div>
    </div>

  );
}

export default Formation352;
