import "./styles.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
import Select from "react-select";
import Formation from "./components/formation";


const formations = [
  { value: "4-4-2", label: "4-4-2" },
  { value: "4-3-3", label: "4-3-3" },
  { value: "4-5-1", label: "4-5-1" },
  { value: "3-5-2", label: "3-5-2" },
  { value: "4-2-3-1", label: "4-2-3-1" }
];



function SquadBuildingPage() {
  const [selectedOption, setSelectedOption] = useState({
    value: "4-4-2",
    label: "4-4-2"
  });
  var formation = selectedOption.value;

  useEffect(() => {
    const fetchData = async () => {
        try {
            const response = await axios.get('/get_formation_data');
            console.log(response.data.formation)
            setSelectedOption({
              value: response.data.formation,
              label: response.data.formation
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };


    fetchData();
}, []);



  return (
    <>
      <div className="App">

        <div className="formationSection">

            <div className="formationOverlay">
              <Formation formation={formation} />
            </div>

        </div>



      </div>
              <div className="formationSelector">
              <Select
                value={selectedOption}
                onChange={setSelectedOption}
                options={formations}
              />
            </div>
            </>
  );
}
export default SquadBuildingPage;
