import React, { useState } from "react";
import Position from "../components/position";
import "./352style.css";
function Formation352() {
  const players = [
    "player1",
    "player2",
    "player3",
    "player4",
    "player5",
    "player6",
    "player7",
    "player8",
    "player9",
    "player10",
    "player11"
  ];
  const initialRoles = [
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role",
    "role"
  ]
/*   const roleToPositionMapping = {
    [players[10]]: [roles[10]],
    [players[9]]: [roles[9]],
    [players[8]]: [roles[8]],
    [players[7]]: [roles[7]],
    [players[6]]: [roles[6]],
    [players[2]]: [roles[2]],
    [players[5]]: [roles[5]],
    [players[4]]: [roles[4]],
    [players[3]]: [roles[3]],
    [players[0]]: [roles[0]],
    [players[1]]: [roles[1]],
  }; */

  // State to manage roles
  const [roles, setRoles] = useState(initialRoles);

  // Function to handle role change
  const handleRoleChange = (playerIndex, newRole) => {
    setRoles(prevRoles => {
      const updatedRoles = [...prevRoles];
      updatedRoles[playerIndex] = newRole;
      return updatedRoles;
    });
  };


  return (
    <div className="formation352">
      <div className="attackLane352">
        <Position playerName={players[0]} positionName="ATTACK" roleToPosition={roles[0]} onRoleChange={(newRole) => handleRoleChange(index, newRole)}/>
        <Position playerName={players[1]} positionName="ATTACK" roleToPosition={roles[1]} onRoleChange={(newRole) => handleRoleChange(index, newRole)}/>
      </div>
      <div className="midLane352">
        <Position playerName={players[2]} positionName="LAT" roleToPosition={roles[2]} />
        <Position playerName={players[3]} positionName="MID" roleToPosition={roles[3]}/>
        <Position playerName={players[4]} positionName="MID" roleToPosition={roles[4]}/>
        <Position playerName={players[5]} positionName="MID" roleToPosition={roles[5]}/>
        <Position playerName={players[6]} positionName="LAT" roleToPosition={roles[6]} />
      </div>
      <div className="backLane352">
        <Position playerName={players[7]} positionName="DEF"roleToPosition={roles[7]} />
        <Position playerName={players[8]} positionName="DEF"roleToPosition={roles[8]} />
        <Position playerName={players[9]} positionName="DEF"roleToPosition={roles[9]} />
      </div>
      <div className="gkLane352">
        <Position playerName={players[10]} positionName="GK"roleToPosition={roles[10]} />
      </div>
    </div>

  );
}

export default Formation352;
