  axios.post('/get_best_team', { selected_roles: selectedRoles })
    .then(response => {
      setBestTeam(response.data);
    })
    .catch(error => {
      console.error('Error fetching the best team:', error);
    });
};

  return (
      <div className="App">
        <div className="formationSection">
          <div className="formationSelector">
            <Select
              defaultValue={selectedOption}
              onChange={setSelectedOption}
              options={formations}
            />
          </div>
          <div>
            <div className="formationOverlay">
              <Formation formation={formation} setRoleToPositionMapping={setRoleToPositionMapping}/>
            </div>
            <div className="pitchBackground">
              <PitchComponent />
            </div>
          </div>
          <button onClick={handleGetBestTeam}>Get Best Team</button>
        {bestTeam && (
          <div>
            <h2>Best Team</h2>
            <pre>{JSON.stringify(bestTeam, null, 2)}</pre>
          </div>
        )}
        </div>
      </div>
  );
}
export default SquadBuildingPage;
