import "./styles.css";

import React, { useState } from "react";
import Select from "react-select";
import PitchComponent from "./components/pitch";
import Formation from "./components/formation";
import axios from "axios";

const formations = [
  { value: "4-4-2", label: "4-4-2" },
  { value: "4-3-3", label: "4-3-3" },
  { value: "4-5-1", label: "4-5-1" },
  { value: "3-5-2", label: "3-5-2" },
  { value: "4-2-3-1", label: "4-2-3-1" }
];



function SquadBuildingPage() {
  const [selectedOption, setSelectedOption] = useState({
    value: "4-4-2",
    label: "4-4-2"
  });
  var formation = selectedOption.value;

  const [bestTeam, setBestTeam] = useState(null);


const handleGetBestTeam = (roleToPositionMapping) => {
  console.log(roleToPositionMapping)
  const selectedRoles = {
    "GK": "G",
    "DEF1": "DC_Def",
    "DEF2": "DC_Stop",
    "DEF3": "DC_Couv",
    "LAT1": "Lat_Comp_S",
    "LAT2": "Lat_Comp_A",
    "MID1": "B2B",
    "MID2": "Meneur_Ret_D",
    "MID3": "MD_Def",
    "ATT1": "Att_Avanc",
    "ATT2": "Att_Comp_S"
  };

  axios.post('/get_best_team', { selected_roles: selectedRoles })
    .then(response => {
      setBestTeam(response.data);
    })
    .catch(error => {
      console.error('Error fetching the best team:', error);
    });
};

  return (
      <div className="App">
        <div className="formationSection">
          <div className="formationSelector">
            <Select
              defaultValue={selectedOption}
              onChange={setSelectedOption}
              options={formations}
            />
          </div>
          <div>
            <div className="formationOverlay">
              <Formation formation={formation} />
            </div>
            <div className="pitchBackground">
              <PitchComponent />
            </div>
          </div>
          <button onClick={handleGetBestTeam(roleToPositionMapping)}>Get Best Team</button>
        {bestTeam && (
          <div>
            <h2>Best Team</h2>
            <pre>{JSON.stringify(bestTeam, null, 2)}</pre>
          </div>
        )}
        </div>
      </div>
  );
}
export default SquadBuildingPage;
