import React, { useState, useEffect } from "react";
import axios from "axios";
import Formation442 from "../formations/formation442.js";
import Formation433 from "../formations/formation433.js";
import Formation352 from "../formations/formation352.js";
import Formation4231 from "../formations/formation4231.js";
import Formation451 from "../formations/formation451.js";
import "../styles.css";

function Formation(props) {
  const { formation, bestTeam, bestTeamRoles, handleRoleChange, setBestTeam, setBestTeamRoles } = props;

  const [loading, setLoading] = useState(true);

  let savedFormation;

  useEffect(() => {
    const fetchDataFormation = async () => {
      try {
          const response = await axios.get('/get_formation_data');
          savedFormation = response.data.formation
          console.log(formation)
          console.log(savedFormation)
          if (savedFormation !== formation) {
            console.log("erase")
            setBestTeam(null);
            setBestTeamRoles(null);
              }
      } catch (error) {
          console.error('Error fetching data:', error);
      } finally {
        setLoading(false); 
      }
    };
    fetchDataFormation();
  }, []);

  if (loading) {
    return <div>Loading...</div>; 
  }

  const formationComponents = {
    "4-4-2": <Formation442
    bestTeam={bestTeam}
    bestTeamRoles={bestTeamRoles}
    handleRoleChange={handleRoleChange}
     />,
    "4-3-3": <Formation433
    bestTeam={bestTeam}
    bestTeamRoles={bestTeamRoles}
    handleRoleChange={handleRoleChange}
     />,
    "3-5-2": (
      <Formation352
      bestTeam={bestTeam}
      bestTeamRoles={bestTeamRoles}
      handleRoleChange={handleRoleChange}
      />
    ),
    "4-2-3-1": 
      <Formation4231
      bestTeam={bestTeam}
      bestTeamRoles={bestTeamRoles}
      handleRoleChange={handleRoleChange}
    />,
    "4-5-1": <Formation451
    bestTeam={bestTeam}
    bestTeamRoles={bestTeamRoles}
    handleRoleChange={handleRoleChange}
     />
  };

  return <div className="formation-select">{formationComponents[formation]}</div>;
}

export default Formation;
